$('input[name="options"]').on("change",function(){switch($(this).attr("data-type")){case"conversion":$("#conversion-container").toggle(),$("#migration-container").toggle();break;case"migration":$("#conversion-container").toggle(),$("#migration-container").toggle();break}}),$("#code-form").submit(function(e){e.preventDefault(),console.log("code-form submitted!");var t=$("#code-editor").val(),o=$("#code-content").val(),a=$("#code-trigger").val(),c=$("#code-scope").val(),n=$("#code-description").val();switch(t){case"atom":populateToText("#snippet-output",createAtom(o,a,c,n));break;case"brackets":populateToText("#snippet-output",createBrackets(o,a,c,n));break;case"sublime":populateToText("#snippet-output",createSublime(o,a,c,n));break;case"visual_code":populateToText("#snippet-output",createVisualCode(o,a,c,n));break;default:}var s=$(this),i=s.attr("action"),r=$.post(i,s.serialize(),function(e){e.success?console.log("Successfully loaded data!",e.success):console.log("Error loading data!",e.error)},"json")});